import React, { useState } from 'react';

const Form = () => {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [classHandling, setClassHandling] = useState('');
  const [subjects, setSubjects] = useState('');

  const addUser = () => {
    const newUser = {
      name,
      email,
      password,
      role,
      classHandling: classHandling.split(','),
      subjects: subjects.split(','),
    };

    setUsers([...users, newUser]);
    setName('');
    setEmail('');
    setPassword('');
    setRole('');
    setClassHandling('');
    setSubjects('');
  };

  return (
    <div className='adduser'>
      
      <h2>User List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Password</th>
            <th>Role</th>
            <th>Class Handling</th>
            <th>Subjects</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.password}</td>
              <td>{user.role}</td>
              <td>{user.classHandling.join(', ')}</td>
              <td>{user.subjects.join(', ')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Form;
