import React from "react";
import { Droppable } from "react-dnd-html5-backend";

const hours = ['9:00', '10:00', '11:00', '12:00', '1:00', '2:00', '3:00', '4:00', '5:00'];
const timeSlots = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

const Table = ({ table, onDrop }) => {
  return (
    <div>
      <Droppable onDrop={onDrop}>
        <table style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Time</th>
              {hours.map((hour, index) => (
                <th key={index}>{hour}</th>
              ))}
            </tr>
            <tr>
              <th>Time</th>
              {timeSlots.map((slot, index) => (
                <th key={index}>{slot}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {daysOfWeek.map((day, dayIndex) => (
              <tr key={dayIndex}>
                <td>{day}</td>
                {hours.map((hour, hourIndex) => (
                  <td
                    key={hourIndex}
                    style={
                      hourIndex === 3 || hourIndex === 5 || hourIndex === 6
                        ? { border: 'none', borderLeft: '1px solid black' }
                        : { border: '1px solid black' }
                    }
                  ></td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Droppable>
    </div>
  );
};

export default Table;
