import Logo from "../elements/logo/Logo";
import Sides from "../elements/Sides";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import login from "../asstes/loginimg.png"
import side from "../asstes/login.png"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./signup.css"


let Signup = () => {
    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isValid, setIsValid] = useState(true);

    const handleEmail = (e) =>{
        setEmail(e.target.value);
        setIsValid(validateEmail(e.target.value));
    }
    const handlePassword = (e) =>{
        setPassword(e.target.value);
    }

    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
      };

    const handleSubmit = async (e) =>{
        e.preventDefault();
        if(email === '' || password === ''){
            toast.warn('Enter all fields ðŸ˜•', {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                });
        }
        else{
        if(!isValid){
            toast.warn('Incorrect Email IDðŸ˜•', {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                });
        }
        else{
          try {
            
          } catch (error) {
            
          }
        }
    }
        console.log(email,password);
    }
    return (
        <div className="signup">
            <div>
                <Logo />
            </div>
            <div className="signup-container">
                <div className="signup-left">
                   <div className="sides">
                   <Sides source={side} />
                   </div>
                </div>
                <div className="signup-right">
                <div className="wrapper">
                    <div>
                        <img src={login} alt="login" />
                    </div>
                    <div className="form">
                        <form onSubmit={handleSubmit}>
                            <input type="text" placeholder="email " onChange={handleEmail} ></input><br />
                            <input type="password" placeholder="password" onChange={handlePassword}></input><br />
                            <button className="signup-button" type="submit" onClick={() => navigate("/admin/dashboard")}>LOGIN</button>
                        </form>
                    </div>
                </div>
                    
                </div>
            </div>
        </div>
    )

}

export default Signup;
