import Logo from "../elements/logo/Logo";
import Sides from "../elements/Sides";
import { useState } from "react";
import login from "../asstes/loginimg.png"
import side from "../asstes/login.png"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
// import "../\"
import "./signup.css"


let Signup = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isValid, setIsValid] = useState(true);

    const handleEmail = (e) =>{
        setEmail(e.target.value);
        setIsValid(validateEmail(e.target.value));
    }
    const handlePassword = (e) =>{
        setPassword(e.target.value);
    }

    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
      };

    const handleSubmit = (e) =>{
        e.preventDefault();
        if(email === ''){
            toast.warn('The Email field cannot be emptyðŸ˜•', {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,

                });
        }
        if(password === ''){
            toast.warn('The Password field cannot be emptyðŸ˜•', {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                });
        }
        console.log(email,password);
    }
    return (
        <div className="signup">
            <div>
                <Logo />
            </div>
            <div className="signup-container">
                <div className="signup-left">
                   <div className="sides">
                   <Sides source={side} />
                   </div>
                </div>
                <div className="signup-right">
                <div className="wrapper">
                    <div>
                        <img src={login} alt="login" />
                    </div>
                    <div className="form">
                        <form onSubmit={handleSubmit}>
                            <input type="text" placeholder="email " onChange={handleEmail} ></input><br />
                            <input type="password" placeholder="password" onChange={handlePassword}></input><br />
                            <button type="submit">LOGIN</button>
                        </form>
                    </div>
                </div>
                    
                </div>
            </div>
        </div>
    )

}

export default Signup;
