import React from 'react';
import { Droppable } from 'react-dnd';

const Table = () => {
  const [table, setTable] = useState([]);

  const onDrop = (subject, index) => {
    // Update the table to include the dropped subject at the given index.
    setTable([...table, subject]);
  };

  return (
    <div>
      <Droppable onDrop={onDrop}>
        <table style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Time</th>
              {hours.map((hour, index) => (
                <th key={index}>{hour}</th>
              ))}
            </tr>
            <tr>
              <th>Time</th>
              {timeSlots.map((slot, index) => (
                <th key={index}>{slot}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {daysOfWeek.map((day, dayIndex) => (
              <tr key={dayIndex}>
                <td>{day}</td>
                {hours.map((hour, hourIndex) => (
                  <td
                    key={hourIndex}
                    style={
                      hourIndex === 3 || hourIndex === 5 || hourIndex === 6
                        ? { border: 'none', borderLeft: '1px solid black' }
                        : { border: '1px solid black' }
                    }
                  ></td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Droppable>
    </div>
  );
};

export default Table;
