// ListDisplay.js
import React from 'react';
import Logo from '../elements/logo/Logo';
import Form from '../elements/Form';
import "./display.css"
import Scrollbars from 'react-custom-scrollbars';
import { useState, useEffect } from 'react';
import warn from '../elements/warn';
const Display = () => {



  

  const [data, setData] = useState([]);
  const [name,setName] = useState("");
  const [department,setDepartment] = useState("")
  const [email,setEmail] = useState("")
  const [year,setYear] = useState("")
  const [cls,setCls] = useState("")
  const [subject,setSubject] = useState("")



  useEffect(() => {
    fetch('http://localhost:5000/list')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.log(error.message));

  }, []);
  const handleSubmit = async () =>{
    const response = await fetch("http://localhost:5000/insert",{
      method: 'POST',
      headers :{
        'Content-Type':'application/json',
      },
      body:JSON.stringify({name:name, department:department, email:email, year:year, cls:cls, subject:subject })
    })
  }

  const addFields = [
    { type: "text", placeholder: "Name", onChange:setName },
    { type: "text", placeholder: "Department", onChange:{setDepartment} },
    { type: "text", placeholder: "Email", onChange:{setEmail} },
    { type: "text", placeholder: "Year", onChange:{setYear} },
    { type: "text", placeholder: "Class", onChange:{setCls} },
    { type: "text", placeholder: "Subject", onChange:setSubject }
  ]


  return (
    <div className='display'>
      <div>
        <Logo />
      </div>
      <div className='displaycontainer' >
        <div className='displayleft'>
          <div className='displayform'>
            <Form fields={addFields} onSubmit={handleSubmit}txt="ADD" marginBottom="10px" />
          </div>
        </div>
        <div className='displayright'>
          <div className='scrollable'>
          <Scrollbars >
          <ul style={{ listStyleType: "none" }} className='list'>
              {data.map((faculty, index) => (
                <li key={index} className='faculty'>
                  <p>Name: {faculty.name}</p>
                  <p>Department: {faculty.department}</p>
                </li>
              ))}
            </ul>
            </Scrollbars>
          </div>
        </div>
      </div>

    </div>
  )
}

export default Display;
