import { toast } from 'react-toastify';

const warn = (text) =>{
    return(
        toast.warn({text}, {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
        })
    )
}

export default warn;
