import React from 'react';
import { useDrag } from 'react-dnd';

const Subject = ({ subject }) => {
  const [{ isDragging }, ref] = useDrag({
    type: 'SUBJECT',
    item: { id: subject.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={ref}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: 'pointer',
      }}
    >
      {subject.name}
    </div>
  );
};

export default Subject;
