import React from "react";
import StaffList from "./components/elements/staffList/StaffList";
import Table from "./Table";

const Timetable = () => {
  const [table, setTable] = React.useState([]);

  const onDrop = (subject, index) => {
    // Update the state to include the dropped subject at the given index.
    const updatedState = [...table];
    updatedState.splice(index, 0, subject);
    setTable(updatedState);
  };

  return (
    <div className="design">
      <div className="sub">
        <StaffList staff={["Math", "Science", "English", "History", "Geography"]} onDrop={onDrop} />
      </div>
      <div className="staffWrapper">
        <div className="staffsss">
          <Table table={table} onDrop={onDrop} />
        </div>
      </div>
    </div>
  );
};

export default Timetable;
